    uint len = tokenIds.length; 
    for(uint i = 0; i < len ; i++){ 
      _burn(tokenIds[i]); 
    } 
  } 

  function burn(uint256 tokenId) external { 
    _burn(tokenId); 
  } 

  function _burn(uint256 tokenId) internal virtual override { 
