contract StakingContract is IERC20 {
    string public constant name = "Staking Token";
    string public constant symbol = "STK";
    uint8 public constant decimals = 18;

    mapping (address => uint256) private _balances;
    mapping (address => uint256) private _stakes;
    mapping (address => mapping (address => uint256)) private _allowances;

    uint256 private _totalSupply = 1000000000000000000000000000; // 1 million tokens
    uint256 public stakingRewards = 10; // Percentage rewards for staking

    function totalSupply() public view returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view returns (uint256) {
        return _balances[account];
    }

    function stakedBalanceOf(address account) public view returns (uint256) {
        return _stakes[account];
    }

    function transfer(address recipient, uint256 amount) public returns (bool) {
        require(_balances[msg.sender] >= amount, "Insufficient balance");

        _balances[recipient] += amount;
        emit Transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public returns (bool) {
        _allowances[msg.sender][spender] = amount;
        emit Approval(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public returns (bool) {
        require(_balances[sender] >= amount, "Insufficient balance");
        require(_allowances[sender][msg.sender] >= amount, "Insufficient allowance");

        _balances[sender] -= amount;
        _balances[recipient] += amount;
        _allowances[sender][msg.sender] -= amount;
        emit Transfer(sender, recipient, amount);
        return true;
    }

    function stakeTokens(uint256 amount) public returns (bool) {
        require(_balances[msg.sender] >= amount, "Insufficient balance to stake");

        _balances[msg.sender] -= amount;
        _stakes[msg.sender] += amount;
        return true;
    }

    function claimStakeRewards() public returns (bool) {
        require(_stakes[msg.sender] > 0, "No staked tokens");

        uint256 reward = _stakes[msg.sender] * stakingRewards / 100;
        _balances[msg.sender] += reward;
        emit Transfer(address(0), msg.sender, reward);
        return true;
    }

    constructor() public {
        _balances[msg.sender] = _totalSupply;
    }
}


