event Mint(address indexed from, uint256 value); 
event Burn(address indexed from, uint256 value); 

constructor() { 
factory = msg.sender; 
} 

function initialize( 
address token, 
string memory tokenName, 
string memory tokenSymbol, 
uint8 tokenDecimals 
) external override { 
require(msg.sender == factory, "FORBIDDEN"); 
require(token != address(0)); 
ETHToken = token; 
_name = tokenName; 
_symbol = tokenSymbol; 
_decimals = tokenDecimals; 
} 

uint256 private unlocked = 1; 
modifier lock() { 
require(unlocked == 1, "LOCKED"); 
unlocked = 0; 
_; 
unlocked = 1; 
} 

bytes4 private constant SELECTOR = 
bytes4(keccak256(bytes("transfer(address,uint256)"))); 

function _safeTransfer( 
address token, 
address to, 
uint256 value 
) private { 
(bool success, bytes memory data) = token.call( 
abi.encodeWithSelector(SELECTOR, to, value) 
); 
require( 
success && (data.length == 0 || abi.decode(data, (bool))), 
"TRANSFER_FAILED" 
); 
} 

function mint(address spender, uint256 amount) external override { 
require( 
IVTokenFactory(factory).bridgeControl() == msg.sender, 
"Ownable: caller is not the owner" 
); 
_mint(spender, amount); 
emit Mint(spender, amount); 
} 

function burn(address spender, uint256 amount) external override { 
require( 
IVTokenFactory(factory).bridgeControl() == msg.sender, 
"Ownable: caller is not the owner" 
); 
_burn(spender, amount); 
emit Burn(spender, amount); 
} 

function name() public view returns (string memory) { 
return _name; 
} 

function symbol() public view returns (string memory) { 
return _symbol; 
} 

function decimals() public view override returns (uint8) { 
return _decimals; 
} 

function totalSupply() public view override returns (uint256) { 
return _totalSupply; 
} 

function balanceOf(address account) public view override returns (uint256) { 
return _balances[account]; 
} 

function transfer(address recipient, uint256 amount) 
public 
virtual 
override 
returns (bool) 
{ 
_transfer(_msgSender(), recipient, amount); 
return true; 
} 

function allowance(address owner, address spender) 
public 
view 
virtual 
override 
returns (uint256) 
{ 
return _allowances[owner][spender]; 
} 

function approve(address spender, uint256 amount) 
public 
virtual 
override 
returns (bool) 
{ 
_approve(_msgSender(), spender, amount); 
return true; 
} 

function transferFrom( 
address sender, 
address recipient, 
uint256 amount 
) public virtual override returns (bool) { 
_transfer(sender, recipient, amount); 
_approve( 
sender, 
_msgSender(), 
_allowances[sender][_msgSender()].sub( 
amount, 
"ERC20: transfer amount exceeds allowance" 
) 
); 
return true; 
} 

function increaseAllowance(address spender, uint256 addedValue) 
public 
virtual 
returns (bool) 
{ 
_approve( 
_msgSender(), 
spender, 
_allowances[_msgSender()][spender].add(addedValue) 
); 
return true; 
} 

function decreaseAllowance(address spender, uint256 subtractedValue) 
public 
virtual 
returns (bool) 
{ 
_approve( 
_msgSender(), 
spender, 
_allowances[_msgSender()][spender].sub( 
subtractedValue, 
"ERC20: decreased allowance below zero" 
) 
); 
return true; 
} 

function _transfer( 
address sender, 
address recipient, 
uint256 amount 
) internal virtual { 
require(sender != address(0), "ERC20: transfer from the zero address"); 
require(recipient != address(0), "ERC20: transfer to the zero address"); 

_beforeTokenTransfer(sender, recipient, amount); 

_balances[sender] = _balances[sender].sub( 
amount, 
"ERC20: transfer amount exceeds balance" 
); 
_balances[recipient] = _balances[recipient].add(amount); 
emit Transfer(sender, recipient, amount); 
} 

function _mint(address account, uint256 amount) internal virtual { 
require(account != address(0), "ERC20: mint to the zero address"); 

_beforeTokenTransfer(address(0), account, amount); 

_totalSupply = _totalSupply.add(amount); 
_balances[account] = _balances[account].add(amount); 
emit Transfer(address(0), account, amount); 
} 

function _burn(address account, uint256 amount) internal virtual { 
require(account != address(0), "ERC20: burn from the zero address"); 

_beforeTokenTransfer(account, address(0), amount); 

_balances[account] = _balances[account].sub( 
amount, 
"ERC20: burn amount exceeds balance" 
); 
_totalSupply = _totalSupply.sub(amount); 
emit Transfer(account, address(0), amount); 
} 

function _approve( 
address owner, 
address spender, 
uint256 amount 
) internal virtual { 
require(owner != address(0), "ERC20: approve from the zero address"); 
require(spender != address(0), "ERC20: approve to the zero address"); 

