uint256 payout;
uint256 vesting;
uint256 lastBlock;
uint256 pricePaid;
}
struct BondPair {
string name;
bool active;
uint256 currentPrice;
uint256 decimals;
uint256 lastDecay;
uint256 incrementRate;
uint256 decrementRate;
uint256 vestingTerm;
uint256 maxPayout;
uint256 buffer;
}
address public immutable earthFundToken;
address payable public immutable treasury;
address public staking;
address public stakingHelper;
bool public useHelper;
