        uint256 highestEthPrice = ethPrice * maxPriceMultiple * (100 + expectedPriceFloatVal ) / 100;
        require ( _expectedPrice <= highestEthPrice , " expectedPrice too high !" ) ;
        currAmount = msg . value * 10 ** angryTokenDecimals / ethPrice ;
        pcInfo . price = ethPrice ;
        pcInfo . paymentAmount = msg . value ;
