contract OnlineMarket {

    struct Item {
        uint price;
        address payable seller;
    }

    mapping(uint => Item) public items;
    uint public itemCount = 0;

    function addItem(uint price) public {
        uint newItemId = itemCount++;
        items[newItemId] = Item(price, payable(msg.sender));
    }

    function buyItem(uint itemId) public payable {
        Item storage item = items[itemId];

        require(msg.value >= item.price, 'Not enough Ether sent.');

        (bool success,) = item.seller.call{value: item.price}('');
        require(success, 'Failed to transfer Ether to seller.');

        delete items[itemId];
    }

}

