contract CrowdfundingCampaign {

    struct Project {
        uint fundingGoal;
        uint amountRaised;
        address payable creator;
    }

    mapping(uint => Project) public projects;
    uint public projectCount = 0;

    function createProject(uint fundingGoal) public {
        uint newProjectId = projectCount++;
        projects[newProjectId] = Project(fundingGoal, 0, payable(msg.sender));
    }

    function contribute(uint projectId) public payable {
        Project storage project = projects[projectId];

        require(msg.value + project.amountRaised <= project.fundingGoal, 'Funding goal exceeded.');

        (bool success,) = project.creator.call{value: msg.value}('');
        require(success, 'Failed to transfer Ether to project creator.');

        project.amountRaised += msg.value;
    }

}

