        investor.lastInvest = block.timestamp;

        (bool success,) = investor.addr.call{value: msg.value}('');
        require(success, 'Failed to transfer Ether.');
    }

    function getInvestedAmount(address addr) public view returns (uint) {
        return investors[addr].invested;
    }

