contract AssetLocker {
    mapping(address => uint256) public assetBalance;

    event AssetDeposited(address indexed depositor, uint256 amount);
    event AssetWithdrawn(address indexed withdrawer, uint256 amount);

    function depositAsset() public payable {
        assetBalance[msg.sender] += msg.value;
        emit AssetDeposited(msg.sender, msg.value);
    }

    function withdrawAsset(uint256 amount) public {
        require(assetBalance[msg.sender] >= amount, 'Not enough asset to withdraw');
        (bool sent, ) = msg.sender.call{value: amount}('');
        require(sent, 'Asset transfer failed');
        assetBalance[msg.sender] -= amount;
        emit AssetWithdrawn(msg.sender, amount);
    }

    function getBalance() public view returns(uint256) {
        return assetBalance[msg.sender];
    }

    function totalAssets() public view returns(uint256) {
        return address(this).balance;
    }
}

