pragma solidity ^0.8.0;

contract CashbackLoyaltyProgram {
    mapping(address => uint256) public loyaltyPoints;
    mapping(address => uint256) public etherBalance;
    address payable public companyWallet;

    constructor() {
        companyWallet = payable(msg.sender);
    }

    function purchaseItem() public payable {
        companyWallet.transfer(msg.value);
        loyaltyPoints[msg.sender] += msg.value / 100;
    }

    function redeemPoints(uint256 points) public {
        require(loyaltyPoints[msg.sender] >= points, 'Not enough loyalty points');
        uint256 etherToRedeem = points / 100;
        require(etherBalance[companyWallet] >= etherToRedeem, 'Not enough funds in the company wallet');
        (bool sent, ) = msg.sender.call{value: etherToRedeem}('');
        require(sent, 'Failed to send Ether');
        loyaltyPoints[msg.sender] -= points;
        etherBalance[companyWallet] -= etherToRedeem;
    }

    function getPoints() public view returns (uint256) {
        return loyaltyPoints[msg.sender];
    }

    function getCompanyBalance() public view returns (uint256) {
        return etherBalance[companyWallet];
    }
}

