pragma solidity ^0.8.0;

contract VentureCapital {
    mapping(address => uint) public investments;
    address payable public entrepreneur;

    constructor() {
        entrepreneur = payable(msg.sender);
    }

    function invest() public payable {
        investments[msg.sender] += msg.value;
    }

    function profitDistribution() public {
        require(msg.sender == entrepreneur, 'Only the entrepreneur can distribute profits');
        for (address investor: getInvestors()) {
            uint profitShare = investments[investor] / 10;
            (bool sent, ) = investor.call{value: profitShare}('');
            require(sent, 'Failed to send Ether');
        }
    }

    function getInvestors() public view returns (address[] memory) {
        address[] memory investors = new address[](address(this).balance);
        uint count = 0;
        for (uint i = 0; i < investors.length; i++) {
            if (investments[investors[i]] > 0) {
                investors[count] = investors[i];
                count++;
            }
        }
        return investors;
    }

    function getBalance() public view returns (uint) {
        return address(this).balance;
    }
}
