contract MemePetToken is StandardToken { 

    string public name;                   
    uint8 public decimals;                
    string public symbol;                 
    string public version = 'PET1.0'; 
    uint256 public unitsOneEthCanBuy;     
    uint256 public totalEthInWei;         
    address public fundsWallet;

function MemePetToken() {
    balances[msg.sender] = 100000000000000000000000000000;
    totalSupply = 100000000000000000000000000000;
    name = "MemePet Token";
    decimals = 18;
    symbol = "PET";
    unitsOneEthCanBuy = 0;
    fundsWallet = msg.sender;
}

function() payable{
    totalEthInWei = totalEthInWei + msg.value;
    uint256 amount = msg.value * unitsOneEthCanBuy;
    require(balances[fundsWallet] >= amount, "Insufficient balance in fundsWallet for the transfer");
    require(totalSupply >= amount, "Insufficient total supply for the transfer");

